import {
	ADD_COLLECTION_SUCCESS, ADD_COLLECTION_FAILURE
} from '../actions/types';

const initialState = {
	loading: true,
	title: '',
	containers: []
};


export default function (state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		case ADD_COLLECTION_SUCCESS:
			return {
				...state,
				containers: payload,
				loading: false,
			};
		case ADD_COLLECTION_FAILURE:
			return {
				...state,
			};
	
		default:
			return state;
	}
}
