import React from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import styled from 'styled-components';
import { logout } from '../../actions/auth';
import { deleteUser } from '../../actions/auth';

import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField';
import Nav from '../../styled/Nav';
import Button from '../../styled/Button';

const OptionsButton = styled(Button)`
	height: 4em;
	width: 15em;
	font-size: 1em;
	margin: 2em auto 1em auto;
	letter-spacing: 2.5px;
	border-radius: 6px;
`;

const handler = () => {};

const Account = ({ logout, deleteUser, isAuthenticated }) => {
	const handler = (func) => {
		if (!isAuthenticated && func == 'deleteUser') {
			deleteUser();
			return <Redirect to='/' />;
		} else {
			logout();
			return <Redirect to='/' />;
		}
	};
	return (
		<DataForm>
			<DataField>
				<OptionsButton onClick={handler}>Logout</OptionsButton>
			</DataField>
			<DataField>
				<OptionsButton onClick={() => handler('deleteUser')}>
					Delete Account
				</OptionsButton>
			</DataField>
		</DataForm>
	);
};
const mapStateToProps = (state) => ({
	logout,
	deleteUser,
	isAuthenticated: state.auth.isAuthenticated,
});
export default connect(mapStateToProps, {
	logout,
	deleteUser,
})(Account);
