import {
	DATA_SUCCESS,
	DATA_FAILURE,
	NYC_DATA_SUCCESS,
	GAURDIAN_DATA_SUCCESS
} from '../actions/types';
const initialState = {
	data: [],
	loading: true,
	error: {}
};

export default function(state = initialState, action) {
	const { type, payload } = action;

	switch (type) {
		case NYC_DATA_SUCCESS:
			return {
				...state,
				data: payload[0],
				loading: false
			};
		case GAURDIAN_DATA_SUCCESS:
			return {
				...state,
				data: payload[1],
				loading: false
			};
		case DATA_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
				data: []
			};
		default:
			return {
				...state
			};
	}
}
