import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const newsItems = (state, nyc, gaurdian) => {
	const news = state((item, index) => {
		return (
			<div key={index}>
				<p>{item.abstract} </p>
			</div>
		);
	});
	return <div>{news}</div>;
