	// 		</div>
	// 	);
	// });
	// return <div>{news}</div>;
	console.log(state);
	return 'card';
};

newsItems.propTypes = {
	nyc: PropTypes.array.isRequired,
	gaurdian: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	nyc: state.nyc.nyc,
	gaurdian: state.gaurdian.gaurdianData
});

export default connect(mapStateToProps)(newsItems);
