import React, {Fragment, useState} from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField';
import Nav from '../../styled/Nav';
import Button from '../../styled/Button';
import BackButton from '../../styled/BackButton'
import Header from '../../styled/Header'

import {addCollection} from '../../actions/collection' 


const OptionsButton = styled(Button)`
	input{
		font-size: 1em;
		letter-spacing: 2.5px;
		border-radius: 6px;
		background-color: white
		border-radius: 0;
	}
	label {
		color: cyan;
		
	}
`;

const CollectionInput = styled(DataField)`
	background-color: #fff;
	width: 250px;
	border: 1px solid lightcyan;
	border-radius: none;
	label {
		color: cyan;
	}
`


const Collection = () => {

	const [formData, setFormData] = useState({
		title: '',
	});

	const { title } = formData;

	const onChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};
	const onSubmit = (e) => {
		e.preventDefault();
		console.log(formData)
		addCollection({ title });
	};

	const history = useHistory();
	const back = () => {
		const path = '/dashboard'
		history.push(path)
	}
	return (
		<Fragment>
				<Nav>
				<DataField>
					<label htmlFor='email'>CollectionTitle</label>
					<input
						type='title'
						name='title'
						
						required
						onChange={(e) => onChange(e)}
						minLength='6'
					/>
				</DataField>
			<OptionsButton>
			<label>Add</label>
			</OptionsButton>
				</Nav>
			
		
			
		<BackButton onClick={back}></BackButton>
		
		</Fragment>
	);
};

export default Collection;
