import React, { useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const DataCard = (props, nycState, gaurdianData) => {
	const nyc = props.nycState;
	const gaurdian = props.gaurdianData;
	const filteredNyc = [];
	const filteredGaurdian = [];

	console.log(nyc);

	// const initialState = {
	// 	filteredNyc: []
	// };

	//	const [filteredNews, setNews] = usestate(initialState);

	if (nyc) {
		const results = nyc.data.results;
		results.map(item => {
			return item.title;
		});
		//results.filter(item => console.log(item.title));
	}

	console.log(filteredNyc);

	filteredNyc.map(item => {
		console.log(item);
	});
	return 'data';
};

DataCard.propTypes = {
	nycState: PropTypes.object.isRequired,
	gaurdianData: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	nycState: state.nyc.nycState,
	gaurdianData: state.gaurdian.gaurdianData
});

export default connect(mapStateToProps)(DataCard);
