import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  html, body {
    margin: 0;
    background: whitesmoke;
    height: 100%;
    width: 100%;
    color: black;
    font: Roboto;
    letter-spacing: 0.4px;
    -webkit-font-smoothing: subpixel-antialiased;
    position: relative;
  }
`;

export default GlobalStyle;
