import { DATA_SUCCESS, DATA_FAILURE } from './types';
import axios from 'axios';

export const getData = () => async dispatch => {
	try {
		const res = await axios.get(
			'https://newsapi.org/v2/top-headlines?country=us&apiKey=b05a05005ec84712a23fe01b017a443b'
		);
		console.log(res);
		dispatch({
			type: DATA_SUCCESS,
			payload: res.data
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: { msg: err.response.statusText, status: err.response.status }
		});
	}
};
