import { DATA_SUCCESS, DATA_FAILURE } from './types';
import axios from 'axios';
import newsHttp from '../utilities/http';

const getData = () => async dispatch => {
	const newsRes = newsHttp();
	try {
		dispatch({
			type: DATA_SUCCESS,
			payload: newsRes
			//payload: res.data.content
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err.response.statusText,
				status: err.response.status
			}
		});
	}
};

export default getData;
