import React, { useEffect } from 'react';
//import { Link, withRouter } from 'react-router-dom';
import { getNyc } from '../actions/getNyc';
import { getGaurdian } from '../actions/getGaurdian';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import DataCard from './DataCard';

const DataView = ({ getNyc, getGaurdian }) => {
	useEffect(() => {
		getNyc();
		getGaurdian();
	}, [getNyc, getGaurdian]);

	//return <DataCard />;
};

DataView.propTypes = {
	getNyc: PropTypes.func.isRequired,
	getGaurdian: PropTypes.func.isRequired,
	nyc: PropTypes.object.isRequired
	//gaurdianData: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
	nyc: state.nyc.nycData
	//: state.gaurdian.gaurdianData
});

export default connect(mapStateToProps, { getNyc, getGaurdian })(DataView);
