import React, { useState, Fragment } from 'react';
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';
import PropTypes from 'prop-types';
import { searchNews } from '../../actions/searchNews';
import { deleteUser } from '../../actions/auth';
import LogoutUser from '../auth/LogoutUser';
import DeleteUser from '../auth/DeleteUser';
import DataView from '../layout/DataView';

import styled from 'styled-components';
import GlobalButton from '../../styled/GlobalButton';
import DataField from '../../styled/DataField';
import Centering from '../../styled/Centering';

const StyledField = styled(DataField)`
	display: inline-block;
	margin: 0;
	width: 0;
	input {
		border-radius: 6px 0 0 6px;
	}
`;

const StyledButton = styled(GlobalButton)`
	display: inline-block;
	width: 10em;
	height: 3em;
	border-radius: 0 6px 6px 0;
	margin: 0;
`;

const NewsResults = styled(DataView)``;

const DashBoard = ({ searchNews, isAuthenticated }) => {
	const [searchTerm, setSearchTerm] = useState('');

	const onChange = (e) => setSearchTerm(e.target.value);

	const onSubmit = (e) => {
		e.preventDefault();
		searchNews(searchTerm);
	};

	return (
		dashboard,
		{
			/*<Centering>
			<LogoutUser />
			<DeleteUser />

			 <StyledField>
				<form className='form' onSubmit={(e) => onSubmit(e)}>
					<input
						type='text'
						name='Search'
						placeholder='Search Term'
						value={searchTerm}
						onChange={onChange}
					/>

					<StyledButton type='submit' value='News Search'>
						Search
					</StyledButton>
				</form>
			</StyledField> 
			<DataView />
		</Centering>*/
		}
	);
};

const mapStateToProps = (state) => ({
	searchNews: PropTypes.func.isRequired,
	currentResults: state.topStories.currentResults,
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { searchNews })(DashBoard);
