import { GAURDIAN_DATA_SUCCESS, DATA_FAILURE } from './types';
import axios from 'axios';
import cors from '../utilities/cors';

export const getGaurdian = () => async dispatch => {
	const gaurdian =
		'https://content.guardianapis.com/search?show-fields=thumbnail&q=coronavirus&page-size=100&api-key=3cb25f91-26b8-4f58-833f-873b0478f441';

	//	cors();
	const gaurdianReq = await axios.get(gaurdian);

	try {
		const gaurdianReq = await axios.get(gaurdian);

		if (!gaurdianReq.data.status === 'OK') {
			throw new Error(`${gaurdianReq.status} ${gaurdianReq.statusText}`);
		}

		dispatch({
			type: GAURDIAN_DATA_SUCCESS,
			payload: gaurdianReq
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err,
				status: err
			}
		});
	}
};
