import React, { useState, Fragment } from 'react';
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';
import PropTypes from 'prop-types';
import { searchNews } from '../../actions/searchNews';
import { deleteUser } from '../../actions/auth';
import LogoutUser from '../auth/LogoutUser';
import DataView from '../layout/DataView';

import styled from 'styled-components';
import GlobalButton from '../../styled/GlobalButton';
import DataField from '../../styled/DataField';
import Centering from '../../styled/Centering';
import Global from '../../styled/Global';
import Nav from '../../styled/Nav';
import Header from '../../styled/Header';
import Footer from '../../styled/Footer';

const StyledField = styled(DataField)`
	display: inline-block;
	margin: 0;
	width: 0;

	input {
		border-radius: 6px 0 0 6px;
	}
`;

const StyledButton = styled(GlobalButton)`
	display: inline-block;
	width: 10em;
	height: 3em;
	border-radius: 0 6px 6px 0;
	margin: 0;
`;

const NewsResults = styled(DataView)``;

const DashBoard = ({ searchNews, isAuthenticated }) => {
	const [searchTerm, setSearchTerm] = useState('');

	const onChange = (e) => setSearchTerm(e.target.value);

	const onSubmit = (e) => {
		e.preventDefault();
		searchNews(searchTerm);
	};

	return (
		<Global>
			<Header>
				<Nav>
					<Button>Search</Button>
					<Button>Collections</Button>
					<Button>Account</Button>
				</Nav>
			</Header>
			<Content />
			<Footer />
		</Global>
	);
};

const mapStateToProps = (state) => ({
	searchNews: PropTypes.func.isRequired,
	currentResults: state.topStories.currentResults,
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { searchNews })(DashBoard);

