import styled from 'styled-components';

export default styled.form`
	transform: translate(-50%, -50%);
	position: absolute;
	top: 50%;
	left: 50%;
	overflow: auto;
	text-align: center;
	margin: 0 auto;
	padding: 0 0 2.5em 1.5em;
`;
