import React, { Fragment, useState, useEffect } from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import styled from 'styled-components'
import Button from '../../styled/Button';
import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField'
import DataCard from '../layout/DataCard'
import BackButton from '../../styled/BackButton'


const StyledForm = styled(DataForm)`
	position: absolute;
	width: 80%;
	top: 120px;
	padding 10px;
	border-bottom: 1px solid lightcyan;
`

const SearchTitle = styled.h3`
	text-align:center;
	letter-spacing: 1.25px;
	margin-bottom: 0.75em;
`
const StyledSubmit = styled(Button)`
	height: 4em;
	width: 15em;
	font-size: 1em;
	margin: 1em auto 1em auto;
	letter-spacing: 2.5px;
	border-radius: 6px;
`;


const Search = ({ logout, deleteUser, isAuthenticated }) => {

	const [searchWord, setSearchTerm] = useState({
		searchTerm: '',
	});

	const { searchTerm } = searchWord;

	const onChange = (e) => {
		setSearchTerm({ ...searchWord, [e.target.name]: e.target.value });
	};

	const onSubmit = async (e) => {
		e.preventDefault();
		console.log({searchTerm})
		//	search(searchTerm);
	};

	const history = useHistory();
 
	const back = () => {
		const path = '/dashboard'
		history.push(path)
	}

	return (
		<Fragment>
			<BackButton onClick={back}></BackButton>
			<StyledForm onSubmit={(e) => onSubmit(e)}>
				<SearchTitle>Search for Articles</SearchTitle>
				<DataField>
				<input
					type='text'
					name='searchTerm'
					value={searchTerm}
					required
					onChange={(e) => onChange(e)}
					minLength='3'
				/>
				</DataField>
				<StyledSubmit>Search</StyledSubmit>	
			</StyledForm>
			<DataCard>Card</DataCard>

		</Fragment>

	);
};

export default Search;
