import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { searchNews } from '../actions/searchNews';

// call news-search with search term //

// create search component form

// onsubmit call newsSearch action

const DashBoard = ({ searchNews }) => {
	const [searchTerm, setSearchTerm] = useState('');

	const onChange = (e) => setSearchTerm(e.target.value);

	const onSubmit = (e) => {
		e.preventDefault();
		searchNews(searchTerm);
		console.log(searchTerm);
	};

	return (
		<form className='form' onSubmit={(e) => onSubmit(e)}>
			<input
				type='text'
				placeholder='Search Term'
				name='searchTerm'
				value={searchTerm}
				onChange={onChange}
			/>
			<input type='submit' value='News Search' />
		</form>
	);
};

const mapStateToProps = (state) => ({
	searchNews: PropTypes.func.isRequired,
	currentResults: state.topStories.currentResults,
});

export default connect(mapStateToProps, { searchNews })(DashBoard);
