import { NYC_DATA_SUCCESS, DATA_FAILURE } from '../actions/types';
const initialState = {
	nycData: [],
	loading: true,
	error: {}
};

export default function(state = initialState, action) {
	const { type, payload } = action;

	switch (type) {
		case NYC_DATA_SUCCESS:
			return {
				...state,
				nycData: payload,
				loading: false
			};
		case DATA_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
				data: []
			};
		default:
			return {
				...state
			};
	}
}
