import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const newsItems = props => {
	const news = props.results.map((item, index) => {
		return (
			<div key={index}>
				<p>{item.abstract} </p>
			</div>
		);
	});
	return <div>{news}</div>;
};

newsItems.propTypes = {
	nyc: PropTypes.array.isRequired
	//gaurdianData: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	nyc: state.nyc.nyc
	//gaurdianData: state.gaurdian.gaurdianData
});

export default connect(mapStateToProps)(newsItems);
