import { NYC_DATA_SUCCESS, DATA_FAILURE } from '../actions/types';

export default function (state, action) {
	const { type, payload } = action;

	switch (type) {
		case NYC_DATA_SUCCESS:
			return {
				...state,
				currentNews: payload,
				loading: false,
			};

		case DATA_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
				data: [],
			};
		default:
			return {
				...state,
			};
	}
}
